TriggerOraclev10g.body:
DECLARE
    p_patient VARCHAR(30);
    BEGIN
    SELECT user INTO p_patient FROM dual;
    IF INSERTING THEN
    DBMS_OUTPUT.PUT_LINE('Patient Record Inserted in the Patient Table by User: '|| p_patient );
    ELSIF DELETING THEN
    DBMS_OUTPUT.PUT_LINE('One patient recod is deleted from the patient table by User: '|| p_patient);
    ELSIF UPDATING THEN
    DBMS_OUTPUT.PUT_LINE('Patient Record is now updated by User: '|| p_patient);
    END IF;
    END;
