TriggerOraclev10g.body:
DECLARE
      drugdescription  drug.drug_desc%TYPE; 
      din     drug.din%TYPE;    
    BEGIN
    din := :NEW.din;
    IF INSERTING THEN
    drugdescription := 'Insert';
    ELSIF UPDATING THEN
    drugdescription := 'Update';
    ELSIF DELETING THEN
    din := :OLD.din;
    drugdescription := 'Delete';
    ELSE
    DBMS_OUTPUT.PUT_LINE('You can not reach this data.');
    END IF;

    END drug_trigger;
